view_spec.script:
var DICT = {" ":"", "y":"у",'a':'а','b':'в','c':'с','e':'е','h':'н','k':'к','m':'м','o':'о','p':'р','t':'т','x':'х','д':'d'}

exports.Task = extend(TolokaHandlebarsTask, function (options) {
    TolokaHandlebarsTask.call(this, options);
}, {
    onRender: function() {
        this.$_initViewer();
        var predict = this.getTask().input_values['predict'];
        console.log(predict);
      	if (predict != null){
        var predict = predict.toLowerCase();
        for (var key in DICT) {
            predict = predict.replace(new RegExp(key, 'g'), DICT[key]);
        }
        var predict = predict.toUpperCase();
        }
        var textEl = $(this.getDOMElement()).find(".input__input");
        if (!this.getAssignment().getWorkspaceOptions().isReviewMode) {
            textEl.val(predict);
            this.getSolution()['output_values']['output'] = predict;
        };
        textEl.on("keyup", function() {
            var textStr = textEl.val();
            var textStr = textStr.toLowerCase();
            for (var key in DICT) {
                textStr = textStr.replace(new RegExp(key, 'g'), DICT[key]);
            }
            var textStr =  textStr.toUpperCase();
            textEl.val(textStr);
        });
    },
    onDestroy: function() {
    },
    $_initViewer() {
        var img = this.getDOMElement().querySelector(".picture__img");
        const options = {
            navbar: 0,
            inline:1,
          	title:0,
            toolbar: {
                zoomIn: 1,
                zoomOut: 1,
                oneToOne: 1,
                reset: 1,
                prev: 0,
                play: 0,
                next: 0,
                rotateLeft: 1,
                rotateRight: 1,
                flipHorizontal: 1,
                flipVertical: 1,
            },
            viewed() {
                // All methods are available here except "show".
                var v = this.viewer;
                var naturalWidth = v['initialImageData']['naturalWidth']
                var parentWidth = v['parentData']['width']
                console.log(naturalWidth, parentWidth)
                this.viewer.zoomTo(1.05*(parentWidth/naturalWidth));
            }
        }
        var viewer = new Viewer(img, options);
    }
});

function extend(ParentClass, constructorFunction, prototypeHash) {
    constructorFunction = constructorFunction || function () {};
    prototypeHash = prototypeHash || {};
    if (ParentClass) {
        constructorFunction.prototype = Object.create(ParentClass.prototype);
    }
    for (var i in prototypeHash) {
        constructorFunction.prototype[i] = prototypeHash[i];
    }
    return constructorFunction;
}

